const { getRoleByUsernameAndPassword, addClient } = require('../services/postsServices');

//Controller per ottenere il ruolo dell'utente
async function getUserRole(req, res) {
  try {
    console.log('Richiesta di login ricevuta');
    console.log('Corpo della richiesta:', req.body);
    const { username, password } = req.body;
    const role = await getRoleByUsernameAndPassword(username, password);
    res.json({ role });
  } catch (error) {
    console.error('Errore nel controller login');
    res.status(400).json({ message: error.message });
  }
}

//Controller per inserire un nuovo cliente
async function createClient(req, res) {
  try {
    const { email, username, passwordHash, ruolo, idAgenzia } = req.body;
    const clientId = await addClient(email, username, passwordHash, ruolo, idAgenzia);
    res.status(201).json({ clientId });
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
}

module.exports = {
  getUserRole,
  createClient
};
